import React from 'react';
import {Heading} from './Heading.js';
import {Board} from './Board.js';

export class Game extends React.Component{

	constructor(props){
		super(props);
		this.state = {
			isRunning: false,
			step: 0,
		};
	}

	render(){
		let boardStyle = {
			margin: 'auto',
			width: 420,
		};

		return (<div>
				<Heading step={this.state.step} />
				<Board size={20} style={boardStyle}/>
			</div>);
	}
}
