import React from 'react';
import {Heading} from './Heading.js';
import {Board} from './Board.js';

export class Game extends React.Component{

	constructor(props){
		super(props);
		this.state = {
			step: 0,
			boardWidth: 420,
			rowLength: 20,
			isRunning: false,
		};
		this.toggleRun = this.toggleRun.bind(this);
	}

	toggleRun(){
		this.setState({
			isRunning: !this.state.isRunning,
		});
	}

	render(){
		const buttonStyle = {
			background: this.state.isRunning ? '#FFD700':'#E6CA2E',
			color: this.state.isRunning ? '#FF00D0':'#E62EC4',
		};

		const divStyle = {
			width: 400,
			textAlign: 'center',
			margin: 'auto',
		};

		return (
				<div>
					<Heading step={this.state.step} />
					<div style={divStyle}>
						<button
							style={buttonStyle} 
							onClick={this.toggleRun}
						>{this.state.isRunning ? "stop":"start"}</button>
					</div>
					<Board 
						size={this.state.rowLength} 
						width={this.state.boardWidth} 
						run={this.state.isRunning} 
					/>
				</div>
				);
	}
}
