import React from 'react';
import {Cell} from './Cell.js';

export class Board extends React.Component{

	renderTable(){

		let table = [];
		for(let i = 0; i < this.props.size; i++){
			let row = [];
			for(let j = 0; j < this.props.size; j++){
				row.push(<Cell />);
			}
			table.push(row);
		}
		return table;
	}

	render(){

		return (<table><tbody>{this.renderTable()}</tbody></table>);
	}
}
