import React from 'react';

export class Cell extends React.Component{

	constructor(props){
		super(props);
		this.state = {
			alive: false,
		};
	       //this.updateLife = this.updateLife.bind(this);	
	}

	handleClick(){
		this.setState({alive: !this.state.alive,});
		let n = this.state.alive ? 0:1;
		this.props.call_back(this.props.row_pos, this.props.col_pos, n);
	}

	/*updateLife(){
		if(this.props.run){
			let n = this.props.neighbors;
			let underpopulation = n < 2;
			let lives_on = (n == 2 || n == 3) && this.state.alive;
			let overpopulation = n > 3;
			let new_birth = n == 3 && !this.state.alive;
			let lv = lives_on || new_birth
			this.setState({alive: lv,});
			this.props.call_back(this.props.row_pos, this.props.col_pos, lv  ? 1:0);
		}
	}

	componentDidMount(){
		this.timerID = setInterval(this.updateLife, 1000);
	}*/
	componentWillReceiveProps(nextProps){
		this.setState({alive: nextProps.alive},);
	}

	render(){
		let bg = this.state.alive ? '#0d0d0d':'#d0d0d0';
		let cellStyle = {
			backgroundColor: bg,
			width: this.props.width,
			height: this.props.width,

		};
		return <td style={cellStyle} onClick={this.handleClick.bind(this)}></td>
	}

	/*componentWillUnmount(){
