import React from 'react';
import {Cell} from './Cell.js';

export class Board extends React.Component{

	constructor(props){
		super(props);
		this.state = {
			isRunning: false,
			cells: this.buildArray(),
		}
	}

	buildArray(){
		let sq = [];
		let row = Array(this.props.size).fill(null);
		for(let i = 0; i < this.props.size; i++){
			sq.push(row);
		}
		return sq;
	}

	renderCell(i, j){
		let cellWidth = Math.round(this.props.width/this.props.size);
		return (
			<Cell 
				width={cellWidth}
			/>
		       );
	}




	renderTable(){

		let table = [];
		for(let i = 0; i < this.props.size; i++){
			let row = [];
			for(let j = 0; j < this.props.size; j++){
				row.push(this.renderCell(i, j));
			}
			table.push(<tr>{row}</tr>);
		}
		return table;
	}

	render(){

		let tableStyle = {
			borderWidth: 2,
			borderStyle: 'solid',
			borderColor: '#232323',
			borderRadius: 5,
			width: this.props.width,
			margin: 'auto',
		}
		return (<table style={tableStyle}><tbody>{this.renderTable()}</tbody></table>);
	}
}
